import Axios from 'axios';
import React, {useState, useEffect} from 'react';
import { useSetRecoilState } from 'recoil';
import {roomId} from '../atoms'

function Room(props) {
  const [rooms, setRooms] = useState([])
  const [roomName, setRoomName] = useState('')
  const setRoomId = useSetRecoilState(roomId)

  const fetchData = async () => {
    const response = await Axios.get(
      'http://localhost:8080/chat/rooms'
    );
    console.log(response.data)
    setRooms(response.data); // 데이터는 response.data 안에 들어있습니다.
  };

  useEffect(() => {
    fetchData();
  },[]);

  const onChange = (e) => {
    setRoomName(e.target.value); // 우선 e.target 에서 name 과 value 를 추출
  };

  const createRoom = () => {
    console.log(roomName)
    if (roomName === ''){
      alert('이름을 입력하세요!')
      return
    }
    Axios.post(`http://localhost:8080/chat/room?name=${roomName}`)
    .then(response=>{
        fetchData()
      }
    ).catch(e=>{
      alert("개설 실패" + e)
      }
    )
  }

  return (
    <div className="App">
        <h1>Hey, there!</h1>
        <ul>{rooms.map(room => <li key={room.roomId}  onClick={setRoomId(room.roomId)} ><a>{room.name}</a></li>)}</ul>

        <label> 방 제목 : &nbsp;
        <input type="text" name="name" onKeyUp={onChange}/><button onClick={createRoom}>개설</button>
        </label>

    </div>
  );
}

export default Room
