import React, { useState } from 'react'
import Axios from 'axios';

function Chat(props){
    const sender = prompt("이름입력")
    const roomId = prompt("방 입력")
    console.log(sender, roomId)

    const [chat, setChat] = useState('')

    const onChange = (e) => {
        setChat(e.target.value); // 우선 e.target 에서 name 과 value 를 추출
      };

    return (
        <div>
            <input type="text" name="name" onKeyUp={onChange} placeholder="메세지 보내기"/><button>입력</button>
        </div>
    )
}

export default Chat
