import Axios from 'axios';
import React, {useState, useEffect} from 'react';
import { useSetRecoilState } from 'recoil';
import {RoomId} from '../atoms'

function Room() {
  const [rooms, setRooms] = useState([])
  const [roomName, setRoomName] = useState('')
  const setRoomId = useSetRecoilState(RoomId)

  const fetchData = async () => {
    const response = await Axios.get(
      'http://localhost:8080/chat/rooms'
    );
    setRooms(response.data); // 데이터는 response.data 안에 들어있습니다.
  };

  useEffect(() => {
    fetchData();
  },[]);

  const onChange = (e) => {
    setRoomName(e.target.value);
  };

  const createRoom = () => {
    console.log(roomName)
    if (roomName === ''){
      alert('이름을 입력하세요!')
      return
    }
    Axios.post(`http://localhost:8080/chat/room?name=${roomName}`)
    .then(res=>{
        console.log(res)
        fetchData();
        ;;
      }
    ).catch(e=>{
      alert("개설 실패" + e)
      }
    )
  }

  return (
    <div>
        <h1>Hey, there!</h1>
        <label> 방 제목 : &nbsp;
        <input type="text" name="name" onKeyUp={onChange} placeholder="여기에 방제를 입력" onKeyPress={e=>{
          if (e.key === "Enter"){
            createRoom()
          }
        }}/><button onClick={createRoom}>개설</button>
        </label>
        <ul>
          {rooms.map(room =>
            <li key={room.roomId} onClick={()=>setRoomId(room.roomId)}>
              {room.name}
            </li>)}
        </ul>
    </div>
  );
}

export default Room
