import Axios from 'axios';
import React, {useState, useEffect, useRef} from 'react';
import {useRecoilState, useSetRecoilState} from 'recoil';
import {RoomId, Sender} from '../atoms'
import SockJsClient from 'react-stomp'

const GET_ROOM_URL = 'http://localhost:8080/chat/rooms'
const GET_USER_URL = 'http://localhost:8080/user/'

function Room() {
    const $websocket = useRef(null)

    const [sender, setSender] = useRecoilState(Sender)
    const [roomList, setRoomList] = useState([])
    const [roomName, setRoomName] = useState('')
    const setRoomId = useSetRecoilState(RoomId)

    const fetchData = async () => {
        const response = await Axios.get(GET_ROOM_URL);
        setRoomList(
            response.data.map(room =>
                <li key={room.roomId}
                    onClick={() => setRoomId(room.roomId)}>
                    {room.name}
                </li>)); // 데이터는 response.data 안에 들어있습니다.
    };

    const login = () => {
        const name = prompt("여기에 이름을 입력 ")
        const response = Axios.post(GET_USER_URL,name);
        console.log(response)
        // setSender(response.data);
        console.log(sender)
    }

    useEffect(() => {
        console.log("get room in room")
        fetchData();
    }, []);

    const onChange = (e) => {
        setRoomName(e.target.value);
    };

    const createRoom = () => {

        if (roomName === '') {
            alert('이름을 입력하세요!')
            return
        }

        $websocket.current.sendMessage(
            "/pub/room",
            JSON.stringify({name: roomName, host: sender.userId}),
            {'Content-Type': 'application/json'})
    }

    return (
        <div>
            <h1>Hey, there!</h1>
            <h4>{sender.name}</h4>
            {sender.userId === '' &&
            <button onClick={login}>로그인 </button>
            }



            <label> 방 제목 : &nbsp;
                <input type="text" name="name" onKeyUp={onChange} placeholder="여기에 방제를 입력" onKeyPress={e => {
                    if (e.key === "Enter") {
                        createRoom()
                    }
                }}/>
                <button onClick={createRoom}>개설</button>
            </label>
            <ul>
                {roomList}
            </ul>
            <SockJsClient
                url="http://localhost:8080/ws-stomp"
                topics={['/sub/room/']}
                onMessage={
                    (msg) => {
                        console.log(sender.userId)
                        console.log(msg.host)
                        if (sender.userId === msg.host)
                            setRoomId(msg.roomId)
                        fetchData()
                    }
                }
                ref={$websocket}/>
        </div>
    );
}

export default Room
