import React, { useState } from 'react'
import SockJS from 'react-stomp'
import Axios from 'axios';

function Chat(props){

    const [chat, setChat] = useState('')

    const onChange = (e) => {
        setChat(e.target.value); // 우선 e.target 에서 name 과 value 를 추출
      };
    var sock = SockJS("http://localhost:8080/ws-stomp");
    var ws = Stomp.over(sock);
    var reconnect = 0;

    const findRoom = function() {
        Axios.get('http://localhost:8080/chat/room/'+this.roomId)
        .then(response => { this.room = response.data; });
    }
    const sendMessage = function() {
        ws.send("http://localhost:8080/pub/chat/message", {}, 
            JSON.stringify({type:'TALK', roomId:this.roomId, sender:this.sender, message:this.message}));
        this.message = '';
    }

    const recvMessage = function(recv) {
        this.messages.unshift({"type":recv.type,"sender":recv.type=='ENTER'?'[알림]':recv.sender,"message":recv.message})
    }


      function connect() {
          // pub/sub event
          ws.connect({}, function(frame) {
              ws.subscribe("/sub/chat/room/"+props.roomId, function(message) {
                  var recv = JSON.parse(message.body);
                  recvMessage(recv);
              });
              ws.send("/pub/chat/message", {}, JSON.stringify({type:'ENTER', roomId: props.roomId, sender: props.sender}));
          }, function(e) {
              if(reconnect++ <= 5) {
                  setTimeout(function() {
                      console.log("connection reconnect");
                      sock = new SockJS("http://localhost:8080/ws-stomp");
                      ws = Stomp.over(sock);
                      connect();
                  },10*1000);
              }
          });
      }
      connect();  

    return (
        <div>
            <input type="text" name="name" onKeyUp={onChange} placeholder="메세지 보내기"/><button>입력</button>
        </div>
    )
}

export default Chat
