import { atom } from "recoil"

export const Rooms = atom({
    key: 'rooms',
    default: []
})

export const RoomId = atom({
    key: 'roomId',
    default: ''
})

export const Sender = atom({
    key: 'sender',
    default: 'Guest'
})
