import React, { useState, useEffect, useRef } from 'react'
import Axios from 'axios';
import SockJsClient from 'react-stomp'


function Chat(props){

    const [room, setRoom] = useState('')

    useEffect(() => {
        Axios.get('http://localhost:8080/chat/room/'+props.roomId)
        .then(response => { 
            setRoom(response.data.name) 
        });
    },[])


    const $websocket = useRef (null); 
    const handleMsg = msg => { console.log (msg); }; 
    const handleClickSendTo = () => { $websocket.current.sendMessage ('/pub/chat/message'); }; 
    const handleClickSendTemplate = () => { $websocket.current.sendMessage ('/Template'); };

    return (
        <div> 
            <h1>방제 : {room}</h1> 
            <SockJsClient 
                url="http://localhost:8080/ws-stomp" 
                // topics={[]} 
                onMessage={msg => 
                    { console.log (msg); }
                } 
                ref={$websocket} 
            /> 
            <button onClick={handleClickSendTo}>SendTo</button> 
            <button onClick={handleClickSendTemplate}>SendTemplate</button> </div>
    )
}

export default Chat
