import React, { useState } from 'react'
import SockJS from 'sockjs-client'
import Axios from 'axios';
import Stomp from 'stompjs'

function Chat(props){

    let chat

    const [msgs, setMsgs] = useState([])
    const [room, setRoom] = useState('')



    let sock = new SockJS("http://localhost:8080/ws-stomp");
    let ws = Stomp.over(sock);
    var reconnect = 0;

    Axios.get('http://localhost:8080/chat/room/'+props.roomId)
    .then(response => { 
        console.log("roomname : " ,response.data.name)
        setRoom(response.data.name) 
    });

    const sendMessage = function() {
        ws.send("http://localhost:8080/pub/chat/message", {}, 
            JSON.stringify({type:'TALK', roomId:props.roomId, sender:props.sender, message:chat}));
        console.log("msg : ",msgs)
        chat = ''

    }

    const recvMessage = function(recv) {
        setMsgs(...msgs, "h2llo"
            // {"type":recv.type,"sender":recv.type=='ENTER'?'[알림]':recv.sender,"message":recv.message}
            )

        console.log("msg : ",msgs)
    }



       // pub/sub event
    ws.connect({}, function(frame) {
        ws.subscribe("http://localhost:8080/sub/chat/room/"+props.roomId, function(message) {
            var recv = JSON.parse(message.body);
                recvMessage(recv);
        });
        ws.send("http://localhost:8080/pub/chat/message", {}, JSON.stringify({type:'ENTER', roomId: props.roomId, sender: props.sender}));
        }, function(e) {
            if(reconnect++ <= 1) {
                setTimeout(function() {
                    console.log("connection reconnect");
                    sock = new SockJS("http://localhost:8080/ws-stomp");
                    ws = Stomp.over(sock);
                },10*1000);
              }
        });



    return (
        <div>
            <h1>방제 : {room}</h1>
            <ul>
                {msgs.map(msg=> <li>{msg.sender} - {msg.message}</li>)}
            </ul>
            <input type="text" name="name" onKeyUp={(e) => chat = e.target.value} placeholder="메세지 보내기"/><button onClick={sendMessage}>입력</button>
        </div>
    )
}

export default Chat
