import React, { useState, useEffect} from 'react'
import Axios from 'axios';
import {useRecoilState} from 'recoil';
import {RoomId} from '../atoms'
import SockJs from 'react-stomp'
import Stomp from 'stompjs';


function Chat(){
    let sockJS = new SockJs("http://localhost:8080/ws-stomp");
    let stompClient = Stomp.over(sockJS);

    const [roomId,setRoomId] = useRecoilState(RoomId)
    const [chat, setChat] = useState('')

    const [room, setRoom] = useState('')
    const [sender, setSender] = useState('')

    const [msgs, setMsgs] = useState([])

    useEffect(()=>{
        let sockJS = new SockJs("http://localhost:8080/ws-stomp");
        let stompClient = Stomp.over(sockJS);
        stompClient.debug= () => {};
        console.log("init")
        stompClient.connect({},()=>{
          stompClient.subscribe(`/sub/chat/room/${roomId}`,(msg)=>{
            console.log(msg.body)
            const newMessage = JSON.parse(msg.body) ;
            recvMessage(newMessage);
          });
        });
        stompClient.send("/pub/chat/message", {}, JSON.stringify({type:'ENTER', roomId:roomId, sender:sender}));
      },[msgs]);

    useEffect(()=>{
        setSender('Guest')
        //setSender(prompt("여기에 이름을 입력"))
    },[])

    useEffect(() => {
        if (roomId !== ''){
            Axios.get(`http://localhost:8080/chat/room/${roomId}`)
            .then(res=> { 
                setRoom(res.data) 
            });
        }
    },[roomId])

    const recvMessage = (recv) => {
        console.log(recv)
        setMsgs([
            {"type":recv.type,"sender":recv.type==='ENTER'?'[알림]':recv.sender,"message":recv.message},
            ...msgs])
    }

    const sendMessage = () => {
        if (chat !== ''){
            stompClient.send("/pub/chat/message", {}, JSON.stringify({type:'TALK', roomId:roomId, sender:sender, message:chat}));
        }
    }

    const onChange = (e) => {
        setChat(e.target.value); // 우선 e.target 에서 name 과 value 를 추출
      };

    return (
        <div> 
            <button onClick={()=>setRoomId('')}>뒤로가기</button>
            <h1>방제 : {room.name}</h1>
            <h2>나 : {sender}</h2>
            <input type="text" name="name"  onKeyUp={onChange} placeholder="여기에 메세지 입력"/><button onClick={sendMessage}>보내기</button>
            <ul>
                {msgs.map(msg => <li>{msg.sender} - {msg.message}</li>)}
            </ul>
        </div>
    )
}


export default Chat
