import Axios from 'axios';
import React, {useState, useEffect} from 'react';

function Room(props) {
  const [rooms, setRooms] = useState([])
  const [roomName, setRoomName] = useState('')

  const fetchData = async () => {
    const response = await Axios.get(
      'http://localhost:8080/chat/rooms'
    );
    console.log(response.data)
    setRooms(response.data); // 데이터는 response.data 안에 들어있습니다.
  };

  useEffect(() => {
    fetchData();
  }, []);

  const onChange = (e) => {
    setRoomName(e.target.value); // 우선 e.target 에서 name 과 value 를 추출
  };

  const createRoom = () => {
    console.log(roomName)
    Axios.post(`http://localhost:8080/chat/room?name=${roomName}`)
    .then(
      alert("개설 완료"),
      fetchData()
    ).catch(e=>{
      alert("개설 실패" + e)
    }
    )
  }


  return (
    <div className="App">
        <h1>Hey, there!</h1>
        <div>{rooms.map(room => <h2 key={room.roomId}>{room.name}</h2>)}</div>

        <label> 방 제목 : &nbsp;
        <input type="text" name="name" onKeyUp={onChange}/><button onClick={createRoom}>개설</button>
        </label>

    </div>
  );
}

export default Room
