import {SET_ROOM} from "../actions";
import { combineReducers} from "redux";

const counterInitialState = {
    room: ''
}

const counter = (state = counterInitialState, action) => {
    switch (action.type){
        case SET_ROOM:
            return {
                ...state,
                room: action.room
            }
        default:
            return state
    }
}

const counterApp = combineReducers({
    counter,
})

export default counterApp;
