import Axios from 'axios';
import React, {useState, useEffect} from 'react';
import './App.css';

function App(props) {
  const [data, setData] = useState(initialState)

  const fetchData = async () => {
    try {
      // 요청이 시작 할 때에는 error 와 users 를 초기화하고
      setError(null);
      setUsers(null);
      // loading 상태를 true 로 바꿉니다.
      setLoading(true);
      const response = await axios.get(
        'http://localhost:8080/chat/rooms'
      );
      setData(response.data); // 데이터는 response.data 안에 들어있습니다.
    } catch (e) {
      setError(e);
    }
    setLoading(false);
  };

  useEffect(() => {
    fetchData();
  }, []);

  return (
    <div className="App">
        <h1>Hey, there!</h1>
        <div>{data.map(p=><h4 key={p.roomId}>{p.name}</h4>)}</div>
        <input type="text" name="name"/><button>개설</button>

    </div>
  );
}

export default App;
