import React, { useState, useEffect} from 'react'
import Axios from 'axios';
import { useRecoilValue } from 'recoil';
import {RoomId} from '../atoms'

function Chat(){

    const roomId = useRecoilValue(RoomId)

    const [room, setRoom] = useState('')
    const [sender, setSender] = useState('')

    const [chat, setChat] = useState('')
    const [msgs, setMsgs] = useState([])

    useEffect(()=>{
        setSender('Guest')
        //setSender(prompt("여기에 이름을 입력"))
    },[])

    useEffect(() => {
        if (roomId !== ''){
            Axios.get(`http://localhost:8080/chat/room/${roomId}`)
            .then(res=> { 
                console.log("res : " + res.data)
                setRoom(res.data) 
            });
        }
    },[roomId])

    const sendMessage = (chat) => {
        setMsgs(...inputs, chat)
    }

    const onChange = (e) => {
        setChat(e.target.value); // 우선 e.target 에서 name 과 value 를 추출
      };

    return (
        <div> 
            <h1>방제 : {room.name}</h1>
            <h2>나 : {sender}</h2>
            <ul>
                {msgs.map(msg => <li>{msg}</li>)}
            </ul>
            <input type="text" name="name" onKeyUp={onChange} placeholder="여기에 메세지 입력"/><button onClick={sendMessage}>보내기</button>
        </div>
    )
}


export default Chat
