import React, { useState, useEffect, useRef} from 'react'
import Axios from 'axios';
import {useRecoilState} from 'recoil';
import {RoomId} from '../atoms'
import SockJsClient from 'react-stomp'

function Chat(){
    const $websocket = useRef (null);

    const [roomId,setRoomId] = useRecoilState(RoomId)
    const [sender, setSender] = useState('Guest')

    const [chat, setChat] = useState('')
    const [room, setRoom] = useState('')

    const [msgs, setMsgs] = useState([])

    useEffect(()=>{
        setSender(prompt())
        if (roomId !== ''){
            Axios.get(`http://localhost:8080/chat/room/${roomId}`)
            .then(res=> { 
                setRoom(res.data) 
            });
        }
      },[roomId]);

    const recvMessage = (recv) => {
        console.log("received")
        setMsgs([
            {"type":recv.type,"sender":recv.type === 'ENTER'?'[알림]':recv.sender,"message":recv.message},
            ...msgs])
    }

    const sendMessage = () => {
        if (chat !== ''){
            $websocket.current.sendMessage (
                '/pub/chat/message',
                JSON.stringify({type:'TALK', roomId:roomId, sender:sender, message:chat}),
                {'Content-Type': 'application/json'}
            )
        }
    }

    const onChange = (e) => {
        setChat(e.target.value);
      };

    return (
        <div> 
            <button onClick={()=>setRoomId('')}>뒤로가기</button>
            <h1>방제 : {room.name}</h1>
            <h2>나 : {sender}</h2>
            <input type="text" name="name"  onKeyUp={onChange} placeholder="여기에 메세지 입력" 
                onKeyPress={e => {if (e.key === "Enter"){
                    sendMessage()
                }}}/>
            <button onClick={sendMessage}>보내기</button>
            <ul>
                {msgs.map(msg => <li>{msg.sender} - {msg.message}</li>)}
            </ul>
            <SockJsClient 
                url="http://localhost:8080/ws-stomp" 
                topics={['/sub/chat/room/'+roomId]} 
                onMessage={msg => recvMessage(msg)}
                ref={$websocket}/> 


        </div>
    )
}


export default Chat
